import PropTypes from "prop-types";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      x: 0,
      y: 0,
    };
  }

  _onMouseMove = (e) => {
    const width = this.refs.titleContainer.clientWidth;
    const height = this.refs.titleContainer.clientHeight;
    const oX = (e.nativeEvent.offsetX / width) * 100;
    const oY = (e.nativeEvent.offsetY / height) * 100;
    // console.log(Math.floor(oX), Math.floor(oY));
    this.setState({
      x: oX,
      y: oY,
    });
  };

  _onMouseOut = () => {
    this.setState({
      x: 0,
      y: 0,
    });
  };

  render() {
    // const {text} = this.props;
    const { x, y } = this.state;
    const maskStyle = {
      "--maskX": x,
      "--maskY": y,
    };
    return (
      <div className="home">
        <div className="heroCenterContainer">
          <div
            className="heroContent titleContainer"
            onMouseMove={this._onMouseMove}
            onMouseOut={this._onMouseOut}
            ref="titleContainer"
            style={maskStyle}
          >
            {/* <Zoom> */}
            <div className="heroText white-text titleWrapper">
              Sophia Orlando
            </div>
            <div className="heroText white-text titleWrapper cloneWrapper">
              Sophia Orlando
            </div>
            {/* </Zoom> */}
          </div>
        </div>
      </div>
    );
  }

